with st.sidebar : 
    st.image('data:image/jpeg;base64,')
    st.subheader("Discover your next favorite movie with our fun and easy-to-use movie recommendation system! 🍿✨ Simply input your preferences and let us do the rest. Sit back, relax, and enjoy personalized movie suggestions tailored just for you! 🎬🎉") 
    st.write('Recommend Movies Based on Your Selection')
    st.link_button('GitHub Link : Source Code' ,url ='https://github.com/Ashish-sinh/Recommendation_systems') 


# load data from the pickle file at Movie_data: 
similarity_fector = pickle.load(open('Movie_data/sf.pkl','rb')) 
df = pickle.load(open('Movie_data/df.pkl','rb'))
img_id = df['imdbId'].values


# design according to you 
def recomendation ( movie_name ):
    movie_index = df[df['title'] == movie_name].index[0] 
    similer_movie_index = sorted(enumerate(similarity_fector[movie_index]),reverse=True, key = lambda x : x[1])[1:6]
    movie_list = [] 
    
    for i in similer_movie_index : 
        movie_list.append(df.iloc[i[0],1])
    return movie_list


Selected_Movie = st.selectbox('Select Movie',df['title'].values)

if st.button('Suggest Movie') :
    for i in recomendation(Selected_Movie):
        st.write('•',i)
        
